// import React from 'react'
// import React, { useState } from "react";
import styled from "styled-components";
// import { LoginForm } from "./loginForm";
// import { motion } from "framer-motion";
// import { AccountContext } from "./accountContext";
// import { SignupForm } from "./signup";
// import { FormContainer } from "./common";


export default function RegisterationForm() {
  return (
    <div>

    

<form>
  <div>
      <label style={{display:'Block'}}>Enter First name:
        <input type="text" />
      </label>
      {/* //////////////////////////////// */}

      <label style={{display:'Block'}}>Enter last name:
        <input type="text" />
      </label>
      {/* /////////////////// */}
      <label
       style={{display:'Block'}}>Enter Date of Birth:
        <input type="date" />
      </label>

      <label
       style={{display:'Block'}}>Education:
        <select name="education" id="education" required>
  <option value="bachlors" >Bachlors</option>
  <option value="Masters">Masters</option>
  <option value="PHD">PHD</option>

  {/* <option value=""></option> */}
</select>
</label>

      <label style={{display:'Block'}}>Institute Name:
        <input type="text" />
      </label>
      <label style={{display:'Block'}}>Degree:
        <input type="text" />

      </label>
 

      <label
       style={{display:'Block'}}>Experience:
        <select name="education" id="education" required>
  <option value="bachlors">0 to 1 year </option>
  <option value="Masters">2 to 3 years</option>
  <option value="Masters">3 to 5 years</option>
  <option value="Masters">above 5 years </option>
  <option value="PHD"></option>

  {/* <option value=""></option> */}
  </select>
</label>


<BoxContainer>
        <TopContainer>
          <BackDrop
            variants={backdropVariants}
            transition={expandingTransition}
            initial={false}
            animate={isExpanded ? "expanded" : "collapsed"}
          />
          {active === "signin" && (
            <>
              <HeaderContainer>
                <HeaderText>Welcome</HeaderText>
                <HeaderText>Back</HeaderText>
              </HeaderContainer>
              <SmallText>Please sign-in to continue!</SmallText>
            </>
          )}
          {active === "signup" && (
            <>
              <HeaderContainer>
                <HeaderText>Create </HeaderText>
                <HeaderText>Account</HeaderText>
              </HeaderContainer>
              <SmallText>Please sign-up to continue!</SmallText>
            </>
          )}
        </TopContainer>
        <InnerContainer>
          {active === "signin" && <LoginForm />}
          {active === "signup" && <SignupForm />}
        </InnerContainer>
      </BoxContainer>



 
      </div>

    </form>

    </div>
  )
}
