// import React from 'react'
import React, { useState } from "react";
import styled from "styled-components";
import { LoginForm } from "./loginForm";
import { motion } from "framer-motion";
// import { AccountContext } from "./accountContext";
import { SignupForm } from "./signup";
import { FormContainer } from "./common";
  
const BoxContainer = styled.div`
  // margin-left: auto;
  width: 350px;
  min-height: 550px;
  display: flex;
  flex-direction: column;
  border-radius: 19px;
  background-color: #fff;
  box-shadow: 0 0 2px rgba(15, 15, 15, 0.28);
  position: relative;
  overflow: hidden;
  margin-top:30px;
  margin-bottom:30px;
`;

const TopContainer = styled.div`
  width: 100%;
  height: 250px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 0 1.8em;
  padding-bottom: 5em;
`;

const BackDrop = styled(motion.div)`
  width: 160%;
  height: 550px;
  position: absolute;
  display: flex;
  flex-direction: column;
  border-radius: 50%;
  transform: rotate(60deg);
  top: -290px;
  left: -70px;
  background: rgb(241, 196, 15);
  background: linear-gradient(to right,#0062cc,#0062cc);
`;

const HeaderContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
`;

const HeaderText = styled.h2`
  font-size: 30px;
  font-weight: 600;
  line-height: 1.24;
  color: #fff;
  z-index: 10;
  padding-left: 20px;
  margin: 0;
`;

const SmallText = styled.h5`
  color: #fff;
  font-weight: 500;
  font-size: 11px;
  z-index: 10;
  margin: 0;
  padding-left: 60px;

  margin-top: 39px;
`;

const InnerContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  padding: 0 1.8em;
`;

const backdropVariants = {
  expanded: {
    width: "233%",
    height: "1050px",
    borderRadius: "20%",
    transform: "rotate(60deg)",
  },
  collapsed: {
    width: "160%",
    height: "550px",
    borderRadius: "50%",
    transform: "rotate(60deg)",
  },
};

const expandingTransition = {
  type: "spring",
  duration: 2.3,
  stiffness: 30,
};





export default function RegisterationForm() {
  return (
    <div>

    

<form>
  <div>
      <label style={{display:'Block'}}>Enter First name:
        <input type="text" />
      </label>
      {/* //////////////////////////////// */}

      <label style={{display:'Block'}}>Enter last name:
        <input type="text" />
      </label>
      {/* /////////////////// */}
      <label
       style={{display:'Block'}}>Enter Date of Birth:
        <input type="date" />
      </label>

      <label
       style={{display:'Block'}}>Education:
        <select name="education" id="education" required>
  <option value="bachlors" >Bachlors</option>
  <option value="Masters">Masters</option>
  <option value="PHD">PHD</option>

  {/* <option value=""></option> */}
</select>
</label>

      <label style={{display:'Block'}}>Institute Name:
        <input type="text" />
      </label>
      <label style={{display:'Block'}}>Degree:
        <input type="text" />

      </label>
 

      <label
       style={{display:'Block'}}>Experience:
        <select name="education" id="education" required>
  <option value="bachlors">0 to 1 year </option>
  <option value="Masters">2 to 3 years</option>
  <option value="Masters">3 to 5 years</option>
  <option value="Masters">above 5 years </option>
  <option value="PHD"></option>

  {/* <option value=""></option> */}
  </select>
</label>


<BoxContainer>
        <TopContainer>
          <BackDrop
            variants={backdropVariants}
            transition={expandingTransition}
            initial={false}
            animate={isExpanded ? "expanded" : "collapsed"}
          />
          {active === "signin" && (
            <>
              <HeaderContainer>
                <HeaderText>Welcome</HeaderText>
                <HeaderText>Back</HeaderText>
              </HeaderContainer>
              <SmallText>Please sign-in to continue!</SmallText>
            </>
          )}
          {active === "signup" && (
            <>
              <HeaderContainer>
                <HeaderText>Create </HeaderText>
                <HeaderText>Account</HeaderText>
              </HeaderContainer>
              <SmallText>Please sign-up to continue!</SmallText>
            </>
          )}
        </TopContainer>
        <InnerContainer>
          {active === "signin" && <LoginForm />}
          {active === "signup" && <SignupForm />}
        </InnerContainer>
      </BoxContainer>



 
      </div>

    </form>

    </div>
  )
}
