// import {GoogleLogin} from 'react-google-login';
// import { useDispatch } from 'react-redux';
// import { useHistory } from 'react-router-dom';
// import Icon  from './icon';
// import useStyles from './styles';
// import * as yup from "yup";
// import { AUTH } from '../../Components/constants/actionTypes';
// import zxcvbn from 'zxcvbn';
// import { useRadioGroup } from '@mui/material/RadioGroup';
// import validator from 'validator'
// import { nodeName } from "jquery";


import React, { useContext,useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Button, Checkbox, Input, Link, Radio, RadioGroup } from '@material-ui/core';
import { FormControl,FormControlLabel,FormLabel  } from '@mui/material';
import { useFormik } from "formik";
import axios from "axios";
import {
  // BoldLink,
  // TextArea,
    // FormError,
  // Validity
  // FieldError,
  // FormSuccess,
  // MutedLink,
  FieldContainer_RF2,
  BoxContainer,
  FieldContainer_RF,
  FormContainer,
  Input_RF,
  Input_RF2,
  Label,
  SubmitButton
}
 from "./common";
import { Marginer } from "./../marginer/index";
import { AccountContext } from "./accountContext";
import './validity.css';






function RegisterationForm(props) {

//   // const [ password, setPassword] = useState('');
//   const testResult = zxcvbn(password);
  
// const num = testResult.score * 100/4;
  


// const [errorMessage, setErrorMessage] = useState('')
// const [success, setSuccess] = useState(null);
// const [error, setError] = useState(null);
  
const onSubmit = async (values) => {
  const { confirmPassword, ...data } = values;

  const response = await axios
    .post("http://127.0.0.1:8000/Kavtech/register/", data)
    .catch((err) => {
      console.log(err)
});

  if (response && response.data) {
    formik.resetForm();
  }
};

const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName:"",
      email: "",
      password: "",
      
    },
    validateOnBlur: true,

  });
  
  
  
  return (
 
<>
<Form>
<br/>
<br/>


<FieldContainer_RF>
<FieldContainer_RF2>
<br/>
<br/>
<BoxContainer>
  
  <FormContainer onSubmit={formik.handleSubmit}>    

  <Label>First Name:   
<br/>
<Input_RF
type='text'
name="firstName"
placeholder="Enter Your Last Name"
 />
</Label>
<br/> 
<Label>Last Name:   
<br/>
<Input_RF 
type='text'
name="lastName"
placeholder="Enter Your Last Name"
required />
</Label>
<br/>
<Label>Email:   

<br/>
 <Input_RF 
 type={'email'}
 name="email"
placeholder="Enter Your Email"
required />
</Label>
<br/>


<Label>Date of Birth:   
<br/>
 <Input_RF 
 type={'date'}
 name="DoB"
placeholder="Enter Your Date of Birth"
required />
</Label>

<br/>
<h3>Education:</h3>
<h6 style={{color:"red"}}>At least Bachlors is mandatory to apply for any job.</h6>
<Label>Bachlors:  
  <br/> 
 <Input_RF2
 type={'text'} 
 name="b_degree"
placeholder="Degree"

required />
 <Input_RF
 type={'text'} 
 name="b_institute"
placeholder="Institute"
required />
</Label>
<br/>

<Label>Masters:  
  <br/> 
 <Input_RF2
 type={'text'} 
 name="M_degree"
placeholder="Degree"

/>
 <Input_RF
 type={'text'} 
 name="m_institute"
placeholder="Institute"

/>
</Label>
<br/>
<Label>PHD:  
  <br/> 
 <Input_RF2
 type='text' 
 name="P_degree"
placeholder="Degree"

/>
 <Input_RF
 type={'text'} 
 name="p_institute"
placeholder="Institute"
/>
</Label>
<br/>

<Label>Experience: 
  <br/>
  <select>
  <option name="Fresher"    value="Fresher" selected>Fresher</option>
  <option name="junior"     value="junior">1-2 years</option>
  <option name="senior"     value="senior">2-5 years</option>
  <option name="seniorMost" value="seniorMost">Above 5 years</option>
</select>
<br/>  
</Label>
<br/>


<Label>Job Openings(Apply For): 
  <br/>
  <Checkbox type="checkbox" value="IT" name="job_opening" /> IT/Infrastructure
  <br/>
        <Checkbox type="checkbox" value="data_eng" name="job_opening" /> Data Engineering
        <br/>
        <Checkbox type="checkbox" value="data_sci" name="job_opening" /> Data Science
        <br/>  
        <Checkbox type="checkbox" value="management" name="job_opening" /> management
        <br/>  
</Label>
<br/>

<Label>CV/Resume:  
  <br/> 
 <Input_RF
 type={'file'} 
 name="file"
placeholder="Enter Your First Name"
required />
</Label>
<br/>

<Label>Test Schedule:
  <br/>

  <FormControl>

  <RadioGroup
    aria-labelledby="demo-radio-buttons-group-label"
    defaultValue="female"
    name="radio-buttons-group"
  >
    <FormControlLabel value="now" control={<Radio />} label="now" />
    <FormControlLabel value="later" control={<Radio />} label="later" />

  </RadioGroup>
</FormControl>


  </Label>




        <br/>
        <br/>
        <br/>

</FormContainer>
</BoxContainer>
</FieldContainer_RF2>
</FieldContainer_RF>


<br/>
<br/>
<br/>

</Form>
</>
  );
 }

export default RegisterationForm
